import { checkExceptions, createWaiter, WaiterState } from "@smithy/util-waiter";
import { DescribeWorkspaceCommand } from "../commands/DescribeWorkspaceCommand";
const checkState = async (client, input) => {
    let reason;
    try {
        const result = await client.send(new DescribeWorkspaceCommand(input));
        reason = result;
        try {
            const returnComparator = () => {
                return result.workspace.status.statusCode;
            };
            if (returnComparator() === "DELETING") {
                return { state: WaiterState.RETRY, reason };
            }
        }
        catch (e) { }
    }
    catch (exception) {
        reason = exception;
        if (exception.name && exception.name == "ResourceNotFoundException") {
            return { state: WaiterState.SUCCESS, reason };
        }
    }
    return { state: WaiterState.RETRY, reason };
};
export const waitForWorkspaceDeleted = async (params, input) => {
    const serviceDefaults = { minDelay: 2, maxDelay: 120 };
    return createWaiter({ ...serviceDefaults, ...params }, input, checkState);
};
export const waitUntilWorkspaceDeleted = async (params, input) => {
    const serviceDefaults = { minDelay: 2, maxDelay: 120 };
    const result = await createWaiter({ ...serviceDefaults, ...params }, input, checkState);
    return checkExceptions(result);
};
