import { checkExceptions, createWaiter, WaiterState } from "@smithy/util-waiter";
import { DescribeClusterCommand } from "../commands/DescribeClusterCommand";
const checkState = async (client, input) => {
    let reason;
    try {
        const result = await client.send(new DescribeClusterCommand(input));
        reason = result;
        try {
            const returnComparator = () => {
                return result.Cluster.Status.State;
            };
            if (returnComparator() === "TERMINATED") {
                return { state: WaiterState.SUCCESS, reason };
            }
        }
        catch (e) { }
        try {
            const returnComparator = () => {
                return result.Cluster.Status.State;
            };
            if (returnComparator() === "TERMINATED_WITH_ERRORS") {
                return { state: WaiterState.FAILURE, reason };
            }
        }
        catch (e) { }
    }
    catch (exception) {
        reason = exception;
    }
    return { state: WaiterState.RETRY, reason };
};
export const waitForClusterTerminated = async (params, input) => {
    const serviceDefaults = { minDelay: 30, maxDelay: 120 };
    return createWaiter({ ...serviceDefaults, ...params }, input, checkState);
};
export const waitUntilClusterTerminated = async (params, input) => {
    const serviceDefaults = { minDelay: 30, maxDelay: 120 };
    const result = await createWaiter({ ...serviceDefaults, ...params }, input, checkState);
    return checkExceptions(result);
};
