import { checkExceptions, createWaiter, WaiterState } from "@smithy/util-waiter";
import { GetStreamingSessionCommand } from "../commands/GetStreamingSessionCommand";
const checkState = async (client, input) => {
    let reason;
    try {
        const result = await client.send(new GetStreamingSessionCommand(input));
        reason = result;
        try {
            const returnComparator = () => {
                return result.session.state;
            };
            if (returnComparator() === "STOPPED") {
                return { state: WaiterState.SUCCESS, reason };
            }
        }
        catch (e) { }
        try {
            const returnComparator = () => {
                return result.session.state;
            };
            if (returnComparator() === "STOP_FAILED") {
                return { state: WaiterState.FAILURE, reason };
            }
        }
        catch (e) { }
    }
    catch (exception) {
        reason = exception;
    }
    return { state: WaiterState.RETRY, reason };
};
export const waitForStreamingSessionStopped = async (params, input) => {
    const serviceDefaults = { minDelay: 5, maxDelay: 900 };
    return createWaiter({ ...serviceDefaults, ...params }, input, checkState);
};
export const waitUntilStreamingSessionStopped = async (params, input) => {
    const serviceDefaults = { minDelay: 5, maxDelay: 900 };
    const result = await createWaiter({ ...serviceDefaults, ...params }, input, checkState);
    return checkExceptions(result);
};
