import { checkExceptions, createWaiter, WaiterState } from "@smithy/util-waiter";
import { GetStreamingSessionStreamCommand, } from "../commands/GetStreamingSessionStreamCommand";
const checkState = async (client, input) => {
    let reason;
    try {
        const result = await client.send(new GetStreamingSessionStreamCommand(input));
        reason = result;
        try {
            const returnComparator = () => {
                return result.stream.state;
            };
            if (returnComparator() === "READY") {
                return { state: WaiterState.SUCCESS, reason };
            }
        }
        catch (e) { }
        try {
            const returnComparator = () => {
                return result.stream.state;
            };
            if (returnComparator() === "CREATE_FAILED") {
                return { state: WaiterState.FAILURE, reason };
            }
        }
        catch (e) { }
    }
    catch (exception) {
        reason = exception;
    }
    return { state: WaiterState.RETRY, reason };
};
export const waitForStreamingSessionStreamReady = async (params, input) => {
    const serviceDefaults = { minDelay: 5, maxDelay: 150 };
    return createWaiter({ ...serviceDefaults, ...params }, input, checkState);
};
export const waitUntilStreamingSessionStreamReady = async (params, input) => {
    const serviceDefaults = { minDelay: 5, maxDelay: 150 };
    const result = await createWaiter({ ...serviceDefaults, ...params }, input, checkState);
    return checkExceptions(result);
};
