import { checkExceptions, createWaiter, WaiterState } from "@smithy/util-waiter";
import { GetEnvironmentTemplateVersionCommand, } from "../commands/GetEnvironmentTemplateVersionCommand";
const checkState = async (client, input) => {
    let reason;
    try {
        const result = await client.send(new GetEnvironmentTemplateVersionCommand(input));
        reason = result;
        try {
            const returnComparator = () => {
                return result.environmentTemplateVersion.status;
            };
            if (returnComparator() === "DRAFT") {
                return { state: WaiterState.SUCCESS, reason };
            }
        }
        catch (e) { }
        try {
            const returnComparator = () => {
                return result.environmentTemplateVersion.status;
            };
            if (returnComparator() === "PUBLISHED") {
                return { state: WaiterState.SUCCESS, reason };
            }
        }
        catch (e) { }
        try {
            const returnComparator = () => {
                return result.environmentTemplateVersion.status;
            };
            if (returnComparator() === "REGISTRATION_FAILED") {
                return { state: WaiterState.FAILURE, reason };
            }
        }
        catch (e) { }
    }
    catch (exception) {
        reason = exception;
    }
    return { state: WaiterState.RETRY, reason };
};
export const waitForEnvironmentTemplateVersionRegistered = async (params, input) => {
    const serviceDefaults = { minDelay: 2, maxDelay: 300 };
    return createWaiter({ ...serviceDefaults, ...params }, input, checkState);
};
export const waitUntilEnvironmentTemplateVersionRegistered = async (params, input) => {
    const serviceDefaults = { minDelay: 2, maxDelay: 300 };
    const result = await createWaiter({ ...serviceDefaults, ...params }, input, checkState);
    return checkExceptions(result);
};
