import { checkExceptions, createWaiter, WaiterState } from "@smithy/util-waiter";
import { GetServiceCommand } from "../commands/GetServiceCommand";
const checkState = async (client, input) => {
    let reason;
    try {
        const result = await client.send(new GetServiceCommand(input));
        reason = result;
        try {
            const returnComparator = () => {
                return result.service.pipeline.deploymentStatus;
            };
            if (returnComparator() === "SUCCEEDED") {
                return { state: WaiterState.SUCCESS, reason };
            }
        }
        catch (e) { }
        try {
            const returnComparator = () => {
                return result.service.pipeline.deploymentStatus;
            };
            if (returnComparator() === "FAILED") {
                return { state: WaiterState.FAILURE, reason };
            }
        }
        catch (e) { }
    }
    catch (exception) {
        reason = exception;
    }
    return { state: WaiterState.RETRY, reason };
};
export const waitForServicePipelineDeployed = async (params, input) => {
    const serviceDefaults = { minDelay: 10, maxDelay: 3600 };
    return createWaiter({ ...serviceDefaults, ...params }, input, checkState);
};
export const waitUntilServicePipelineDeployed = async (params, input) => {
    const serviceDefaults = { minDelay: 10, maxDelay: 3600 };
    const result = await createWaiter({ ...serviceDefaults, ...params }, input, checkState);
    return checkExceptions(result);
};
